function calculateAge(){let e=!1,d=document.getElementById("dayin").value.trim(),a=document.getElementById("monthin").value.trim(),l=document.getElementById("yearin").value.trim();var r=/^(0[1-9]|[12][0-9]|3[01])$/,m=/^(0[1-9]|1[0-2]|[1-9])$/,o=/^((19|20)\d\d)$/;if(r.test(d))e=!0;else{e=!1;var t="Enter a valid day";document.getElementById("dayin").value="",document.getElementById("dayin").style.border=" 1px solid red",document.getElementById("dayin").placeholder=t}if(m.test(a))e=!0;else{e=!1;var t="Enter a valid month";document.getElementById("monthin").value="",document.getElementById("monthin").style.border=" 1px solid red",document.getElementById("monthin").placeholder=t}if(o.test(l))e=!0;else{e=!1;var t="Enter a valid year";document.getElementById("yearin").value="",document.getElementById("yearin").style.border=" 1px solid red",document.getElementById("yearin").placeholder=t}if(e===!0){var u=document.getElementById("dayin").value,y=document.getElementById("monthin").value,i=document.getElementById("yearin").value,v=new Date(i,y-1,u),c=new Date,n=c-v,g=Math.floor(n/31536e6),E=Math.floor(n%31536e6/2628e6),B=Math.floor(n%31536e6%2628e6/864e5);document.getElementById("yearoutext").innerText=g,document.getElementById("monthoutext").innerText=E,document.getElementById("dayoutext").innerText=B}}document.getElementById("subm").addEventListener("click",calculateAge);
